svg.script:
var Calendar = new function()
{
  var CurDate = new Date();
  var CalDate = new Date();
  var Months = new Array("January","February","March","April","May","June","July","August","September","October","November","December");
  var Days = new Array(31,28,31,30,31,30,31,31,30,31,30,31);

  window.addEventListener("load", function(e) { generateCalendar(); }, false);

  function generateCalendar()
  {
    var i;
    var suffix = "-5";
    var dy;

    CalDate.setDate(1);

    var dow = CalDate.getDay();
    var month = CalDate.getMonth();
    var year = CalDate.getFullYear();
    if(year%400==0 || (year%100!=0 && year%4==0)) Days[1]=29;

    var node = document.getElementById("month");
    node.firstChild.nodeValue = Months[month]+" "+year;

    for(i=0;i<35;i++)
    {
      node = document.getElementById("B"+(i+1)+suffix);
      if(!(node.childNodes.length==0))
      {
        node.firstChild.nodeValue = ' ';
      }
    }

    dy=45;
    if((dow+Days[month])>35)
    {
      node = document.getElementById("FiveRows");
      node.setAttribute("style","display:none;");
      node = document.getElementById("SixRows");
      node.setAttribute("style","display:default;");
      suffix = "-6";
      dy=38;
    }

    for(i=0;i<Days[month];i++)
    {
      node = document.getElementById("B"+(i+1+dow)+suffix);
      if(node.childNodes.length==0)
      {
      	var textnode = document.createTextNode(' ');
      	node.appendChild(textnode);
      }
      node.firstChild.nodeValue = i+1;
      if(i+1==CurDate.getDate())
      {
        node.setAttribute("style","fill:red;font-size:40px;");
        node.setAttribute("dy","12");
      }
    }
  }
}();
